document.querySelectorAll(".reply-btn").forEach(e=>{e.addEventListener("click",()=>{const c=e.getAttribute("data-message-id");document.querySelector("#parent-id").value=c,e.parentNode.appendChild(document.querySelector("#reply-form")),document.querySelector("#reply-form").style.display="block"})});document.querySelector("#cancel-btn").addEventListener("click",()=>{document.querySelector("#reply-form").style.display="none"});const o=localStorage.getItem("darkMode")==="true",t=document.querySelector('.theme-switch input[type="checkbox"]');o&&(t.checked=!0,document.documentElement.setAttribute("data-theme","dark"));t.addEventListener("change",function(e){e.target.checked?(document.documentElement.setAttribute("data-theme","dark"),localStorage.setItem("darkMode","true")):(document.documentElement.setAttribute("data-theme","light"),localStorage.setItem("darkMode","false"))});
